    displayMarkers: function displayMarkers(positions) {
      var _this = this;

      // 1. 현재 표시되어있는 marker들이 있다면 marker에 등록된 map을 없애준다.
      if (this.markers.length > 0) {
        this.markers.forEach(function (item) {
          item.setMap(null);
        });
      } // 2. 마커 이미지 커스터마이징 하기
      // javascript 영역에서 assets의 정보 가져오기


