mapper.select:
select count(userId)
from user
where userId = #{userId};

select *
from user
where userId = #{userId}
  and userPwd = #{userPwd};

select userId
from user
where userName = #{userName}
  and phone = #{phone};

select userPwd
from user
where userId = #{userId}
  and userName = #{userName}
  and phone = #{phone};

mapper.update:
update user
set userPwd=#{userPwd},
    userName=#{userName},
    email=#{email},
    phone=#{phone}
where userId = #{userId};

mapper.select:
select *
from user
where userId = #{userId}
  and userPwd = #{userPwd};

select likeId
from houselike
where userId = #{userId}
  and aptCode = #{aptCode};
